{
	"commandLine": [ "$(engine.path)\\\\revitcoreconsole.exe /i $(args[rvtFile].path) /al $(appbundles[FileUpgraderApp].path)" ],
	"parameters": {
	  "rvtFile": {
		"zip": false,
		"ondemand": false,
		"verb": "get",
		"description": "Input Revit model",
		"required": true
	  },
	  "resultrvt": {
		"zip": false,
		"ondemand": false,
		"verb": "put",
		"description": "upgraded revit Rvt file",
		"required": false,
		"localName": "revitupgrade.rvt"
	  },
	  "resultrfa": {
		"zip": false,
		"ondemand": false,
		"verb": "put",
		"description": "upgraded revit Rfa file",
		"required": false,
		"localName": "revitupgrade.rfa"
	  },
	  "resultrte": {
		"zip": false,
		"ondemand": false,
		"verb": "put",
		"description": "upgraded revit Rte file",
		"required": false,
		"localName": "revitupgrade.rte"
	  }
	},
	"engine": "Autodesk.Revit+2019",
	"appbundles": [ "{{dasNickName}}.FileUpgraderApp+prod" ],
	"description": "Upgrade revit file to lastest version from old versions."
}

